mapper.select:
SELECT BID,BNAME,BTITLE,BCONTENT,BDATE,
BHIT,BGROUP,BSTEP,BINDENT
FROM REPLYBOARD WHERE BID=#{param1}

mapper.update:
UPDATE REPLYBOARD 
	SET BNAME=#{param2},BTITLE=#{param3},
	BCONTENT=#{param4}	WHERE BID=#{param1}

