mapper.update:
UPDATE hicgv_customer
SET
PASSWORD= #{password},PHONE_NUMBER= #{phone_number},NICKNAME= #{nickname},GENDER= #{gender},
DATE_BIRTH= #{date_birth},LOCATION= #{location},EMAIL= #{email},QUESTION= #{question},ANSWER= #{answer}
WHERE ID LIKE #{id}

